import { useState } from "react"

const BookFilters = ({onFilter}) =>{

    const [titleText, setTitleText] = useState("")
    const [authorText, setAuthorText] = useState("")
    const [filterTitle, setFilterTitle] = useState(false)
    const [filterAuthor, setFilterAuthor] = useState(false)
    const [filterGenre, setFilterGenre] = useState(false)
    const [filterAvailable, setFilterAvailable] = useState(false)
    const [filterAll, setFilterAll] = useState(false)

    const onSubmit = (e) => {
        e.preventDefault()

        if(!titleText){
            alert("please add a task")
            return
        }

        onFilter({titleText, authorText,reminder})
        setTitleText("")
        setAuthorText("")
        setFilterTitle(false)
        setFilterAuthor(false)
        setFilterGenre(false)
        setFilterAvailable(false)
        setFilterAll(false)
    }

    return(
        <div className ="container"> 
            <form className="add-form" onSubmit={onSubmit}>

                <div className="form-control form-control-check">
                    <label>Filter Title</label>
                    <input type="checkbox" checked={filterTitle}
                    value={filterTitle} onChange={(e) => setFilterTitle(e.currentTarget.checked)}></input>
                </div>
                <div className="form-control form-control-check">
                    <label>Filter Author</label>
                    <input type="checkbox" checked={filterTitle}
                    value={filterAuthor} onChange={(e) => setFilterAuthor(e.currentTarget.checked)}></input>
                </div>
                <div className="form-control form-control-check">
                    <label>Filter Genre</label>
                    <input type="checkbox" checked={filterTitle}
                    value={filterGenre} onChange={(e) => setFilterGenre(e.currentTarget.checked)}></input>
                </div>
                <div className="form-control form-control-check">
                    <label>Filter Title</label>
                    <input type="checkbox" checked={filterTitle}
                    value={filterTitle} onChange={(e) => setFilterTitle(e.currentTarget.checked)}></input>
                </div>
                <div className="form-control form-control-check">
                    <label>Filter Title</label>
                    <input type="checkbox" checked={filterTitle}
                    value={filterTitle} onChange={(e) => setFilterTitle(e.currentTarget.checked)}></input>
                </div>
                <div className="form-control form-control-check">
                    <label>Filter Title</label>
                    <input type="checkbox" checked={filterTitle}
                    value={filterTitle} onChange={(e) => setFilterTitle(e.currentTarget.checked)}></input>
                </div>
                <input type="submit" value="Save Task" className="btn btn-block"></input>
            </form>

        </div>
    )
}

export default BookFilters
