import { useState, useEffect } from 'react'
import BookTable from './BookTable';
import FilterBooks from './FilterBooks';
import Axios from "axios";
import './general.css'


const Book = () =>{
    const [showAll, setShowAll] = useState(true)
    const [books, setBooks] = useState([{
      bookID: 1,
      bookTitle: '"13 1/2"',
      author: 'Barr Nevada',
      bookCopies: 2,
      libraryName: 'Central Library'
    }]);

    useEffect(() => { 
      const getBooks = async () => {
          const data = await fetchBooks()
          //console.log(data)
      }
      getBooks()
    }, [])

    const fetchBooks = async() =>{
      console.log("this happens first")
      await Axios.get("http://localhost:3001/books").then((response) => {
        setBooks(response.data)
        return response.data
      })
    }

    const filterShow = async()=>{
      if(showAll){
        await Axios.get("http://localhost:3001/books/all").then((response) => {
         setBooks(response.data)
        })
      }
      else{
        await Axios.get("http://localhost:3001/books").then((response) => {
         setBooks(response.data)
        })
      }

      setShowAll(!showAll)
    }




  return (
    <div>
      <FilterBooks showAll = {!showAll} onShow={filterShow}/>
    <h1 className = "onlyCenter">Book List</h1>
    {books.length > 0 ? <BookTable books = {books}/> : "No Books To Show"}
    </div>
  );
}

export default Book;
