import { useState, useEffect } from 'react'
import BookHistoryTable from './BookHistoryTable';
import Axios from "axios";
import './general.css'
import Button from './Button';

const BookHistory = () =>{
    const [books, setBooks] = useState([{
      bookID: 1,
      bookTitle: '"13 1/2"',
      author: 'Barr Nevada',
      bookCopies: 2,
      libraryName: 'Central Library'
    }]);

    useEffect(() => { 
      const getBooks = async () => {
          const data = await fetchBooks()
      }
      getBooks()
    }, [])

    const fetchBooks = async() =>{
      await Axios.get("http://localhost:3001/user/history").then((response) => {
        setBooks(response.data)
        return response.data
      })
    }

    const getRecommendedBooks = async() =>{
      await Axios.get("http://localhost:3001/user/Recommened").then((response) => {
        setBooks(response.data)
      })
    }




  return (
    <div>
    <h1>Borrowed Book List</h1>
    {books.length > 0 ? <BookHistoryTable books = {books} /> : "No Books To Show"}

    <header className="header">
      <Button color = "DarkOrange" text="Generate my Recommended Books" onClick={getRecommendedBooks}/>
      </header>


    </div>
  );
}

export default BookHistory;
